library ConeLibrary {

    function getAmountOut(
        IConeRouter01 coneRouter,
        address inputToken,
        address outputToken,
        bool isStablePair,
        uint256 amountInput
    ) internal view returns (uint256) {

        IConeRouter01.Route[] memory route = new IConeRouter01.Route[](1);
        route[0].from = inputToken;
        route[0].to = outputToken;
        route[0].stable = isStablePair;

        uint[] memory amounts = coneRouter.getAmountsOut(amountInput, route);

        return amounts[1];
    }

    function getAmountsOut(
        IConeRouter01 coneRouter,
        address inputToken,
        address middleToken,
        address outputToken,
        bool isStablePair0,
        bool isStablePair1,
        uint256 amountInput
    ) internal view returns (uint256) {

        IConeRouter01.Route[] memory route = new IConeRouter01.Route[](2);
        route[0].from = inputToken;
        route[0].to = middleToken;
        route[0].stable = isStablePair0;
        route[1].from = middleToken;
        route[1].to = outputToken;
        route[1].stable = isStablePair1;

        uint[] memory amounts = coneRouter.getAmountsOut(amountInput, route);

        return amounts[2];
    }

    function swap(
        IConeRouter01 coneRouter,
        address inputToken,
        address outputToken,
        bool isStablePair,
        uint256 amountIn,
        uint256 amountOutMin,
        address recipient
    ) internal returns (uint256) {

        IERC20(inputToken).approve(address(coneRouter), amountIn);

        IConeRouter01.Route[] memory route = new IConeRouter01.Route[](1);
        route[0].from = inputToken;
        route[0].to = outputToken;
        route[0].stable = isStablePair;

        uint[] memory amounts = coneRouter.swapExactTokensForTokens(
            amountIn,
            amountOutMin,
            route,
            recipient,
            block.timestamp
        );

        return amounts[1];
    }

    function swap(
        IConeRouter01 coneRouter,
        address inputToken,
        address middleToken,
        address outputToken,
        bool isStablePair0,
        bool isStablePair1,
        uint256 amountIn,
        uint256 amountOutMin,
        address recipient
    ) internal returns (uint256) {

        IERC20(inputToken).approve(address(coneRouter), amountIn);

        IConeRouter01.Route[] memory route = new IConeRouter01.Route[](2);
        route[0].from = inputToken;
        route[0].to = middleToken;
        route[0].stable = isStablePair0;
        route[1].from = middleToken;
        route[1].to = outputToken;
        route[1].stable = isStablePair1;

        uint[] memory amounts = coneRouter.swapExactTokensForTokens(
            amountIn,
            amountOutMin,
            route,
            recipient,
            block.timestamp
        );

        return amounts[2];
    }

}

