pragma solidity ^0.8.0;

import "../StrategyBusdWbnb.sol";

import "@overnight-contracts/common/contracts/libraries/OvnMath.sol";
import "@overnight-contracts/common/contracts/libraries/AaveBorrowLibrary.sol";
import "@overnight-contracts/connectors/contracts/stuff/Cone.sol";
import "@overnight-contracts/connectors/contracts/stuff/Unknown.sol";
import "@overnight-contracts/connectors/contracts/stuff/Venus.sol";
import "@overnight-contracts/connectors/contracts/stuff/KyberSwap.sol";
import "@overnight-contracts/connectors/contracts/stuff/PancakeV2.sol";

import "hardhat/console.sol";

library BusdWbnbLibrary {


    /**
     * ActionType: ADD_LIQUIDITY
     * Add liquidity to cone pool:
     * [wbnbn, busd] -> cone lpToken
     * + stake lpToken to Unknown
     */
    function _addLiquidity(StrategyBusdWbnb self, uint256 delta) public {
        if (self.wbnb().balanceOf(address(self)) == 0 || self.usdPlus().balanceOf(address(self)) == 0) {
            return;
        }
        uint256 busdBalanceBefore = self.busd().balanceOf(address(self));
        self.exchange().redeem(address(self.busd()), self.usdPlus().balanceOf(address(self)) - (delta == self.MAX_UINT_VALUE() ? 0 : delta));
        uint256 busdBalanceAfter = self.busd().balanceOf(address(self));


        self.coneRouter().addLiquidity(
            address(self.wbnb()),
            address(self.busd()),
            false,
            self.wbnb().balanceOf(address(self)),
            busdBalanceAfter - busdBalanceBefore,
            0,
            0,
            address(self),
            block.timestamp
        );


        uint256 lpTokenBalance = self.conePair().balanceOf(address(this));
        self.conePair().approve(address(self.unkwnUserProxy()), lpTokenBalance);
        self.unkwnUserProxy().depositLpAndStake(address(self.conePair()), lpTokenBalance);
    }

    /**
     * ActionType: REMOVE_LIQUIDITY
     * Remove liquidity from cone pool:
     * cone lpToken -> [Wbnb, busd]
     * @param delta - Wbnb amount in USD e6
     */
    function _removeLiquidity(StrategyBusdWbnb self, uint256 delta) public returns (uint256, uint256) {

        uint256 lpForUnstake;
        {
        uint256 poolTokenDelta = self.control().usdToWbnb(delta);
        address userProxyThis = self.unkwnLens().userProxyByAccount(address(this));
        address stakingAddress = self.unkwnLens().stakingRewardsByConePool(address(self.conePair()));
        uint256 balanceLp = IERC20(stakingAddress).balanceOf(userProxyThis);
        (uint256 poolToken,) = _getLiquidityByLp(self, balanceLp);
        lpForUnstake = poolTokenDelta * balanceLp / poolToken + 1;
        self.unkwnUserProxy().unstakeLpAndWithdraw(address(self.conePair()), lpForUnstake);
        }

        uint256 busdBalanceBefore = self.busd().balanceOf(address(self));

        (uint256 amountWbnb, uint256 amountBusd) = self.coneRouter().removeLiquidity(
            address(self.wbnb()),
            address(self.busd()),
            false,
            lpForUnstake,
            0,
            0,
            address(self),
            block.timestamp
        );

        uint256 busdBalanceAfter = self.busd().balanceOf(address(self));
        uint256 usdpBalanceBefore = self.usdPlus().balanceOf(address(self));
        self.exchange().buy(address(self.busd()), busdBalanceAfter - busdBalanceBefore);
        return (amountWbnb, self.usdPlus().balanceOf(address(self)) - usdpBalanceBefore);
    }


    /**
     * ActionType: SWAP_USDPLUS_TO_ASSET
     * Swap on exchange
     * usdPlus -> busd
     * @param delta - UsdPlus in USD e6
     */
    function _swapUspPlusToBusd(StrategyBusdWbnb self, uint256 delta) public {
        uint256 redeemUsdPlusAmount = (delta == self.MAX_UINT_VALUE()) ? self.usdPlus().balanceOf(address(self)) : (self.control().usdToBusd(delta) / 10 ** 12);
        if (redeemUsdPlusAmount == 0) return;
        self.exchange().redeem(address(self.busd()), redeemUsdPlusAmount);
    }


    /**
     * ActionType: SWAP_ASSET_TO_USDPLUS
     * Swap on exchange
     * busd -> usdPlus
     * @param delta - Busd in USD e6
     */
    function _swapBusdToUsdPlus(StrategyBusdWbnb self, uint256 delta) public {
        uint256 buyBusdAmount = (delta == self.MAX_UINT_VALUE()) ? self.busd().balanceOf(address(self)) : (self.control().usdToBusd(delta));
        if (buyBusdAmount == 0) return;
        self.exchange().buy(address(self.busd()), buyBusdAmount);
    }


    /**
     * ActionType: SUPPLY_ASSET_TO_AAVE
     * busd -> (supply aave)
     * @param delta - Busd in USD e6
     */
    function _supplyBusdToVenus(StrategyBusdWbnb self, uint256 delta) public {
        uint256 supplyBusdAmount = (delta == self.MAX_UINT_VALUE()) ? self.busd().balanceOf(address(self)) : self.control().usdToBusd(delta);
        if (supplyBusdAmount == 0) return;
        self.busd().approve(address(self.vBusdToken()), supplyBusdAmount);
        self.vBusdToken().mint(supplyBusdAmount);
    }


    /**
     * ActionType: WITHDRAW_ASSET_FROM_AAVE
     * (aave) -> busd
     * @param delta - Busd in USD e6
     */
    function _withdrawBusdFromVenus(StrategyBusdWbnb self, uint256 delta) public {
        uint256 withdrawBusdAmount = self.control().usdToBusd(delta);
        self.vBusdToken().redeemUnderlying(withdrawBusdAmount);
    }


    /**
     * ActionType: BORROW_TOKEN_FROM_AAVE
     * (borrow from aave) -> wbnb
     * @param delta - Wbnb in USD e6
     */
    function _borrowWbnbFromVenus(StrategyBusdWbnb self, uint256 delta) public {
        uint256 borrowTokenAmount = self.control().usdToWbnb(delta);
        self.vBnbToken().borrow(borrowTokenAmount);
        IWbnb(address(self.wbnb())).deposit{ value: address(this).balance }();
    }


    /**
     * ActionType: REPAY_TOKEN_TO_AAVE
     * wbnb -> (back to aave)
     * @param delta - Wbnb in USD e6
     */
    function _repayWbnbToVenus(StrategyBusdWbnb self, uint256 delta) public {
        uint256 repayWbnbAmount = (delta == self.MAX_UINT_VALUE()) ? self.wbnb().balanceOf(address(self)) : self.control().usdToWbnb(delta);
        if (repayWbnbAmount == 0) return;
        IWbnb(address(self.wbnb())).withdraw(repayWbnbAmount);
        self.maximillion().repayBehalfExplicit{ value: address(this).balance }(address(this), address(self.vBnbToken()));
    }


    /**
     * ActionType: SWAP_TOKEN_TO_ASSET
     * Swap on pancake
     * wbnb -> busd
     * @param delta - Wbnb in USD e6
     */
    function _swapTokenToAsset(StrategyBusdWbnb self, uint256 delta, uint256 slippagePercent) public {
        uint256 swapWbnbAmount = (delta == self.MAX_UINT_VALUE()) ? self.wbnb().balanceOf(address(self)) : self.control().usdToWbnb(delta);
        if (swapWbnbAmount == 0) return;

        uint256 amountOutMin = self.control().usdToBusd(self.control().wbnbToUsd(swapWbnbAmount / 10000 * (10000 - slippagePercent)));

        PancakeSwapLibrary.swapExactTokensForTokens(
                    self.pancakeRouter(),
                    address(self.wbnb()),
                    address(self.busd()),
                    swapWbnbAmount,
                    amountOutMin,
                    address(this)
                );

        self.busd().approve(address(self.coneRouter()), type(uint256).max);
        self.wbnb().approve(address(self.coneRouter()), type(uint256).max);

    }


    /**
     * ActionType: SWAP_ASSET_TO_TOKEN
     * Swap on pancake
     * busd -> wbnb
     * @param delta - BUSD in USD e6
     * example tx: https://bscscan.com/tx/0xd029b94ab61421a1126d29236632c6ce6869d3e753ad857d6b9f55576752ca6a
     */
    function _swapAssetToToken(StrategyBusdWbnb self, uint256 delta, uint256 slippagePercent) public {
        uint256 swapAssetAmount = (delta == self.MAX_UINT_VALUE()) ? self.busd().balanceOf(address(self)) : self.control().usdToBusd(delta);
        if (swapAssetAmount == 0) return;

        uint256 amountOutMin = self.control().usdToWbnb(self.control().busdToUsd(swapAssetAmount / 10000 * (10000 - slippagePercent)));

        PancakeSwapLibrary.swapExactTokensForTokens(
                    self.pancakeRouter(),
                    address(self.busd()),
                    address(self.wbnb()),
                    swapAssetAmount,
                    amountOutMin,
                    address(this)
                );

        self.busd().approve(address(self.coneRouter()), type(uint256).max);
        self.wbnb().approve(address(self.coneRouter()), type(uint256).max);
    }


    /**
     * Own liquidity in pool in their native digits. Used in strategy.
     */
    function _getLiquidity(StrategyBusdWbnb self) public view returns (uint256, uint256) {
        address userProxyThis = self.unkwnLens().userProxyByAccount(address(this));
        address stakingAddress = self.unkwnLens().stakingRewardsByConePool(address(self.conePair()));
        uint256 balanceLp = IERC20(stakingAddress).balanceOf(userProxyThis);
        return _getLiquidityByLp(self, balanceLp);
    }

    function _getLiquidityByLp(StrategyBusdWbnb self, uint256 balanceLp) internal view returns (uint256, uint256) {

         (uint256 reserve0Current, uint256 reserve1Current,) = self.conePair().getReserves();

         uint256 amountLiq0 = reserve0Current * balanceLp / self.conePair().totalSupply();
         uint256 amountLiq1 = reserve1Current * balanceLp / self.conePair().totalSupply();
         return (amountLiq0, amountLiq1);
    }

}
","keccak256":"0x606d411cba0f0f29aadc9ac346d229403caf321c9ca7a8450c744fb90e15b6c1","license":"MIT"},"contracts/libraries/EtsCalculationLibrary.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;


import "hardhat/console.sol";

enum ActionType {
    ADD_LIQUIDITY, // D2, D6 < 0
    REMOVE_LIQUIDITY, // D2, D6 > 0

    SWAP_USDPLUS_TO_ASSET, // D1 < 0
    SWAP_ASSET_TO_USDPLUS, // D1 > 0

    SUPPLY_ASSET_TO_AAVE, // D4 > 0
    WITHDRAW_ASSET_FROM_AAVE, // D4 < 0

    BORROW_TOKEN_FROM_AAVE, // D5 > 0
    REPAY_TOKEN_TO_AAVE, // D5 < 0

    SWAP_TOKEN_TO_ASSET, // D3 < 0
    SWAP_ASSET_TO_TOKEN  // D3 > 0
}


enum Method {
    NOTHING,
    STAKE,
    UNSTAKE
}

// Amounts in decimals specific token, all positive
struct Amounts {
    uint256 collateralAsset;
    uint256 borrowToken;
    uint256 poolToken;
    uint256 poolUsdPlus;
    uint256 freeUsdPlus;
    uint256 freeAsset;
    uint256 freeToken;
}

// liquidity in USD e6, all positive
struct Liquidity {
    int256 collateralAsset;
    int256 borrowToken;
    int256 poolToken;
    int256 poolUsdPlus;
    int256 freeUsdPlus;
    int256 freeAsset;
    int256 freeToken;
}

// liquidity deltas in USD e6, may contain zeroes and below zero
struct Deltas {
    int256 d1;
    int256 d2;
    int256 d3;
    int256 d4;
    int256 d5;
    int256 d6;
    uint256 code;
}

struct Action {
    ActionType actionType;
    uint256 amount;
    uint256 slippagePercent;
}

struct Action2 {
    uint256 actionType;
    uint256 amount;
    uint256 slippagePercent;
}

struct CalcContext {
    int256 K1; // in e18
    int256 K2; // in e18
    int256 amount; // amount in USD, below zero if UNSTAKE
    Liquidity liq; // in USD e6
    uint256 tokenAssetSlippagePercent;
    Deltas deltas; // in USD e6
}

struct CalcContext2 {
    int256 K1; // in e18
    int256 K2; // in e18
    int256 amount; // amount in USD, below zero if UNSTAKE
    Liquidity liq; // in USD e6
    uint256 tokenAssetSlippagePercent;
}


library EtsCalculationLibrary{

    uint256 public constant MAX_UINT_VALUE = type(uint256).max;

    /**
      * d1 = -d2 - Lam - LFu;
      */
    function calcD1(CalcContext memory ctx) internal pure {
        ctx.deltas.d1 = - ctx.deltas.d2 - ctx.amount - ctx.liq.freeUsdPlus;
    }

    /**
      * d2 = d6 * K2;
      */
    function calcD2(CalcContext memory ctx) internal pure {
        ctx.deltas.d2 = ctx.deltas.d6 * ctx.K2 / 1e18;
    }

    /**
      * d3 = -LFw - d5 - d6;
      */
    function calcD3(CalcContext memory ctx) internal pure {
        ctx.deltas.d3 = - ctx.liq.freeToken - ctx.deltas.d5 - ctx.deltas.d6;
    }

    /**
      * d4 = K1 * (LBw + d5) - LCu;
      */
    function calcD4(CalcContext memory ctx) internal pure {
        ctx.deltas.d4 = (ctx.liq.borrowToken + ctx.deltas.d5) * ctx.K1 / 1e18 - ctx.liq.collateralAsset;
    }

    /**
      * d5 = LPw - LBw - d6;
      */
    function calcD5(CalcContext memory ctx) internal pure {
        ctx.deltas.d5 = ctx.liq.poolToken - ctx.liq.borrowToken - ctx.deltas.d6;
    }

    /**
     * d6 = ((K1 - 1) * LPw + LBw - LCu - LFu - LFuc - LFw - Lam) / (K1 + K2);
     */
    function calcD6(CalcContext memory ctx) internal view {

        logSign("K1:", ctx.K1);
        logSign("K2:", ctx.K2);


        int256 a = (ctx.K1 - 1e18) * ctx.liq.poolToken / 1e18;
        logSign("a:", a);
        a = a + ctx.liq.borrowToken;
        logSign("a:", a);
        a = a - ctx.liq.collateralAsset;
        logSign("a:", a);
        a = a - ctx.liq.freeUsdPlus;
        logSign("a:", a);
        a = a - ctx.liq.freeAsset;
        logSign("a:", a);
        a = a - ctx.liq.freeToken;
        logSign("a:", a);
        a = a - ctx.amount;
        logSign("a:", a);

        int256 b = ctx.K1 + ctx.K2;
        logSign("b:", b);
        int256 d6 = 1e18 * a / b;

        ctx.deltas.d6 = d6;
    }

    function logSign(string memory msg, int256 value) internal view {
        if (value < 0) {
            console.log(msg, "-", toUint256(- value));
        } else {
            console.log(msg, toUint256(value));
        }
    }

    function abs(int256 value) internal pure returns (uint256){
        if (value < 0) {
            return toUint256(- value);
        } else {
            return toUint256(value);
        }
    }

    function toUint256(int256 value) internal pure returns (uint256) {
        require(value >= 0, "SafeCast: value must be positive");
        return uint256(value);
    }

    function toInt256(uint256 value) internal pure returns (int256) {
        // Note: Unsafe cast below is okay because `type(int256).max` is guaranteed to be positive
        require(value <= uint256(type(int256).max), "SafeCast: value doesn't fit in an int256");
        return int256(value);
    }


    /**
      * NAV = sum of all tokens liquidity minus borrows.
      * @return NAV in USD
      */
    function _netAssetValue(Liquidity memory liq) internal pure returns (int256){

        // add liquidity in free tokens
        int256 navUsd = liq.freeUsdPlus + liq.freeAsset + liq.freeToken;
        // add liquidity in pool
        navUsd = navUsd + liq.poolToken + liq.poolUsdPlus;
        // add liquidity in aave collateral minus borrow
        navUsd = navUsd + liq.collateralAsset - liq.borrowToken;

        return navUsd;
    }

    function test() public view returns (uint256) {
        return 0;
    }


    function liquidityToActions(CalcContext2 memory ctx2) public view returns (Action[] memory, uint256) {

        Deltas memory deltas = Deltas(0, 0, 0, 0, 0, 0, 0);
        CalcContext memory ctx = CalcContext(ctx2.K1, ctx2.K2, ctx2.amount, ctx2.liq, ctx2.tokenAssetSlippagePercent, deltas);

        //if it is unstake then increase unstake amount to awoid slippages on swap
        if (ctx.amount < 0) {
            //if decrease on tokenAssetSlippagePercent then newAmount became oldAmount
            ctx.amount = ctx.amount * 10000 / (10000 - toInt256(ctx.tokenAssetSlippagePercent));
        }

        // order specified, don't change
        calcD6(ctx);
        calcD2(ctx);
        calcD1(ctx);
        calcD5(ctx);
        calcD4(ctx);
        calcD3(ctx);
        ctx.deltas.code = (ctx.deltas.d1 >= 0 ? 1 : 0) + 2 * (ctx.deltas.d2 >= 0 ? 1 : 0) + 4 * (ctx.deltas.d3 >= 0 ? 1 : 0) + 8 * (ctx.deltas.d4 >= 0 ? 1 : 0) + 16 * (ctx.deltas.d5 >= 0 ? 1 : 0);


        console.log("-------- Deltas");
        logSign("d1:", ctx.deltas.d1);
        logSign("d2:", ctx.deltas.d2);
        logSign("d3:", ctx.deltas.d3);
        logSign("d4:", ctx.deltas.d4);
        logSign("d5:", ctx.deltas.d5);
        logSign("d6:", ctx.deltas.d6);
        console.log("code:", ctx.deltas.code);
        console.log("--------");


        Action[] memory actions = new Action[](6);
        uint index;

        deltas = ctx.deltas;


        if (deltas.d1 < 0 && deltas.d3 < 0 && deltas.d4 < 0
            || deltas.d1 > 0 && deltas.d3 > 0 && deltas.d4 > 0
        ) {
            revert("D1-D3-D4 have same sign");
        }

        if (deltas.d3 < 0 && deltas.d5 < 0 && deltas.d6 < 0
            || deltas.d3 > 0 && deltas.d5 > 0 && deltas.d6 > 0
        ) {
            revert("D3-D5-D6 have same sign");
        }

        if (deltas.d3 >= 0 && deltas.d4 < 0 && deltas.d5 < 0) {
            // first circle cases (4 cases)
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.WITHDRAW_ASSET_FROM_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action((deltas.d1 < 0) ? ActionType.SWAP_USDPLUS_TO_ASSET : ActionType.SWAP_ASSET_TO_USDPLUS, abs(deltas.d1), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_TOKEN, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, (ctx2.amount < 0) ? uint(-ctx2.amount) : MAX_UINT_VALUE, 0);
        } else if (deltas.d3 < 0 && deltas.d4 >= 0 && deltas.d5 >= 0) {
            // second circle cases (4 cases)
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d3), 0);
            actions[index++] = Action(ActionType.SWAP_TOKEN_TO_ASSET, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action((deltas.d1 < 0) ? ActionType.SWAP_USDPLUS_TO_ASSET : ActionType.SWAP_ASSET_TO_USDPLUS, abs(deltas.d1), 0);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, (ctx2.amount < 0) ? uint(-ctx2.amount) : MAX_UINT_VALUE, 0);
        } else if (deltas.d1 >= 0 && deltas.d2 >= 0 && deltas.d3 < 0) {
            // unstake without circle (3 cases: 00011,01011,10011)
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d6), 0);
            actions[index++] = Action((deltas.d5 < 0) ? ActionType.REPAY_TOKEN_TO_AAVE : ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.SWAP_TOKEN_TO_ASSET, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action((deltas.d4 < 0) ? ActionType.WITHDRAW_ASSET_FROM_AAVE : ActionType.SUPPLY_ASSET_TO_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS , MAX_UINT_VALUE, 0);
        } else if (deltas.d1 < 0 && deltas.d2 < 0 && deltas.d3 >= 0) {
            // stake without circle (3 cases: 01100,10100,11100)
            actions[index++] = Action(ActionType.SWAP_USDPLUS_TO_ASSET, abs(deltas.d1), 0);
            actions[index++] = Action((deltas.d4 < 0) ? ActionType.WITHDRAW_ASSET_FROM_AAVE : ActionType.SUPPLY_ASSET_TO_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_TOKEN, MAX_UINT_VALUE, ctx.tokenAssetSlippagePercent);
            actions[index++] = Action((deltas.d5 < 0) ? ActionType.REPAY_TOKEN_TO_AAVE : ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, MAX_UINT_VALUE, 0);
        } else if (deltas.d1 < 0 && deltas.d2 >= 0 && deltas.d4 >= 0 && deltas.d5 < 0) {
            // balance without circle (2 cases: 01010,01110)
            actions[index++] = Action(ActionType.REMOVE_LIQUIDITY, abs(deltas.d6), 0);
            actions[index++] = Action(ActionType.SWAP_USDPLUS_TO_ASSET, abs(deltas.d1), 0);
            actions[index++] = Action((deltas.d3 < 0) ? ActionType.SWAP_TOKEN_TO_ASSET : ActionType.SWAP_ASSET_TO_TOKEN, abs(deltas.d3), ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.SUPPLY_ASSET_TO_AAVE, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.REPAY_TOKEN_TO_AAVE, MAX_UINT_VALUE, 0);
        } else if (deltas.d1 >= 0 && deltas.d2 < 0 && deltas.d4 < 0 && deltas.d5 >= 0) {
            // balance without circle (2 cases: 10001,10101)
            actions[index++] = Action(ActionType.WITHDRAW_ASSET_FROM_AAVE, abs(deltas.d4), 0);
            actions[index++] = Action(ActionType.BORROW_TOKEN_FROM_AAVE, abs(deltas.d5), 0);
            actions[index++] = Action((deltas.d3 < 0) ? ActionType.SWAP_TOKEN_TO_ASSET : ActionType.SWAP_ASSET_TO_TOKEN, abs(deltas.d3), ctx.tokenAssetSlippagePercent);
            actions[index++] = Action(ActionType.SWAP_ASSET_TO_USDPLUS, MAX_UINT_VALUE, 0);
            actions[index++] = Action(ActionType.ADD_LIQUIDITY, (ctx2.amount < 0) ? uint(-ctx2.amount) : MAX_UINT_VALUE, 0);
        } else {
            revert("non-existent case");
        }

        // reassemble array
        Action[] memory tmp = new Action[](index);
        for (uint j; j < index; j++) {
            tmp[j] = actions[j];
        }
        actions = tmp;

        console.log("--------- actions");
        for (uint j; j < actions.length; j++) {
            console.log(j, uint(actions[j].actionType), actions[j].amount);
        }
        console.log("---------");
        return (actions, ctx.deltas.code);
    }

}


