     * `batchSwap` can be used to make a single swap, like `swap` does, but doing so requires more gas than the
     * equivalent `swap` call.
     *
     * Emits `Swap` events.
     */
    function batchSwap(
        SwapKind kind,
        BatchSwapStep[] memory swaps,
        IAsset[] memory assets,
        FundManagement memory funds,
        int256[] memory limits,
        uint256 deadline
    ) external payable returns (int256[] memory);

    /**
     * @dev Data for each individual swap executed by `batchSwap`. The asset in and out fields are indexes into the
     * `assets` array passed to that function, and ETH assets are converted to WETH.
     *
     * If `amount` is zero, the multihop mechanism is used to determine the actual amount based on the amount in/out
     * from the previous swap, depending on the swap kind.
     *
     * The `userData` field is ignored by the Vault, but forwarded to the Pool in the `onSwap` hook, and may be
     * used to extend swap behavior.
     */
    struct BatchSwapStep {
        bytes32 poolId;
        uint256 assetInIndex;
        uint256 assetOutIndex;
        uint256 amount;
        bytes userData;
    }

    /**
     * @dev All tokens in a swap are either sent from the `sender` account to the Vault, or from the Vault to the
     * `recipient` account.
     *
     * If the caller is not `sender`, it must be an authorized relayer for them.
     *
     * If `fromInternalBalance` is true, the `sender`'s Internal Balance will be preferred, performing an ERC20
     * transfer for the difference between the requested amount and the User's Internal Balance (if any). The `sender`
     * must have allowed the Vault to use their tokens via `IERC20.approve()`. This matches the behavior of
     * `joinPool`.
     *
     * If `toInternalBalance` is true, tokens will be deposited to `recipient`'s internal balance instead of
     * transferred. This matches the behavior of `exitPool`.
     *
     * Note that ETH cannot be deposited to or withdrawn from Internal Balance: attempting to do so will trigger a
     * revert.
     */
    struct FundManagement {
        address sender;
        bool fromInternalBalance;
        address payable recipient;
        bool toInternalBalance;
    }

    /**
     * @dev Simulates a call to `batchSwap`, returning an array of Vault asset deltas. Calls to `swap` cannot be
     * simulated directly, but an equivalent `batchSwap` call can and will yield the exact same result.
     *
     * Each element in the array corresponds to the asset at the same index, and indicates the number of tokens (or ETH)
     * the Vault would take from the sender (if positive) or send to the recipient (if negative). The arguments it
     * receives are the same that an equivalent `batchSwap` call would receive.
     *
     * Unlike `batchSwap`, this function performs no checks on the sender or recipient field in the `funds` struct.
     * This makes it suitable to be called by off-chain applications via eth_call without needing to hold tokens,
     * approve them for the Vault, or even know a user's address.
     *
     * Note that this function is not 'view' (due to implementation details): the client code must explicitly execute
     * eth_call instead of eth_sendTransaction.
     */
    function queryBatchSwap(
        SwapKind kind,
        BatchSwapStep[] memory swaps,
        IAsset[] memory assets,
        FundManagement memory funds
    ) external returns (int256[] memory assetDeltas);
}


interface IGauge {

    function balanceOf(address account) view external returns (uint256);

    function deposit(uint256 _amount) external;

    function withdraw(uint256 wad) external;

    function claim_rewards() external;
}


/**
 * @dev This contract simply builds on top of the Balancer V2 architecture to provide useful helpers to users.
 * It connects different functionalities of the protocol components to allow accessing information that would
 * have required a more cumbersome setup if we wanted to provide these already built-in.
 */
interface IBalancerHelpers {

    function queryJoin(
        bytes32 poolId,
        address sender,
        address recipient,
        IVault.JoinPoolRequest memory request
    ) external returns (uint256 bptOut, uint256[] memory amountsIn);

    function queryExit(
        bytes32 poolId,
        address sender,
        address recipient,
        IVault.ExitPoolRequest memory request
    ) external returns (uint256 bptIn, uint256[] memory amountsOut);

}


interface IBalancerMinter {
    /**
     * @notice Mint everything which belongs to `msg.sender` and send to them
     * @param gauge `LiquidityGauge` address to get mintable amount from
     */
    function mint(address gauge) external returns (uint256);

    /**
     * @notice Mint everything which belongs to `msg.sender` across multiple gauges
     * @param gauges List of `LiquidityGauge` addresses
     */
    function mintMany(address[] calldata gauges) external returns (uint256);

    /**
     * @notice Mint tokens for `user`
     * @dev Only possible when `msg.sender` has been approved by `user` to mint on their behalf
     * @param gauge `LiquidityGauge` address to get mintable amount from
     * @param user Address to mint to
     */
    function mintFor(address gauge, address user) external returns (uint256);

    /**
     * @notice Mint tokens for `user` across multiple gauges
     * @dev Only possible when `msg.sender` has been approved by `user` to mint on their behalf
     * @param gauges List of `LiquidityGauge` addresses
     * @param user Address to mint to
     */
    function mintManyFor(address[] calldata gauges, address user) external returns (uint256);

    /**
     * @notice The total number of tokens minted for `user` from `gauge`
     */
    function minted(address user, address gauge) external view returns (uint256);
}


library AequinoxLibrary {

    function queryBatchSwap(
        IVault vault,
        IVault.SwapKind kind,
        IERC20 token0,
        IERC20 token1,
        bytes32 poolId0,
        uint256 amount0,
        address sender,
        address recipient
    ) internal returns (uint256) {

        IVault.BatchSwapStep[] memory swaps = new IVault.BatchSwapStep[](1);
        swaps[0] = IVault.BatchSwapStep(poolId0, 0, 1, amount0, new bytes(0));

        IAsset[] memory assets = new IAsset[](2);
        assets[0] = IAsset(address(token0));
        assets[1] = IAsset(address(token1));

        IVault.FundManagement memory fundManagement = IVault.FundManagement(sender, false, payable(recipient), false);

        return uint256(- vault.queryBatchSwap(kind, swaps, assets, fundManagement)[1]);
    }

    function swap(
        IVault vault,
        IVault.SwapKind kind,
        IERC20 token0,
        IERC20 token1,
        bytes32 poolId0,
        uint256 amount0,
        address sender,
        address recipient
    ) internal returns (uint256) {

        token0.approve(address(vault), amount0);

        IVault.SingleSwap memory singleSwap = IVault.SingleSwap(poolId0, kind, IAsset(address(token0)), IAsset(address(token1)), amount0, new bytes(0));

        IVault.FundManagement memory fundManagement = IVault.FundManagement(sender, false, payable(recipient), false);

        uint256 limit;
        if (kind == IVault.SwapKind.GIVEN_IN) {
            limit = 1e27;
        }

        return vault.swap(singleSwap, fundManagement, limit, block.timestamp);
    }

    function batchSwap(
        IVault vault,
        IVault.SwapKind kind,
        IERC20 token0,
        IERC20 token1,
        IERC20 token2,
        bytes32 poolId0,
        bytes32 poolId1,
        uint256 amount0,
        address sender,
        address recipient
    ) internal returns (uint256) {

        token0.approve(address(vault), amount0);

        IVault.BatchSwapStep[] memory swaps = new IVault.BatchSwapStep[](2);
        swaps[0] = IVault.BatchSwapStep(poolId0, 0, 1, amount0, new bytes(0));
        swaps[1] = IVault.BatchSwapStep(poolId1, 1, 2, 0, new bytes(0));

        IAsset[] memory assets = new IAsset[](3);
        assets[0] = IAsset(address(token0));
        assets[1] = IAsset(address(token1));
        assets[2] = IAsset(address(token2));

        IVault.FundManagement memory fundManagement = IVault.FundManagement(sender, false, payable(recipient), false);

        int256[] memory limits = new int256[](3);
        if (kind == IVault.SwapKind.GIVEN_IN) {
            limits[0] = 1e27;
            limits[1] = 1e27;
            limits[2] = 1e27;
        }

        return uint256(- vault.batchSwap(kind, swaps, assets, fundManagement, limits, block.timestamp)[2]);
    }

    /**
     * Get amount of token1 nominated in token0 where amount0Total is total getting amount nominated in token0
     *
     * precision: 0 - no correction, 1 - one correction (recommended value), 2 or more - several corrections
     */
    function getAmount1InToken0(
        IVault vault,
        IERC20 token0,
        IERC20 token1,
        bytes32 poolId0,
        uint256 amount0Total,
        uint256 reserve0,
        uint256 reserve1,
        uint256 denominator0,
        uint256 denominator1,
        uint256 precision
    ) internal returns (uint256 amount1InToken0) {
        amount1InToken0 = (amount0Total * reserve1) / (reserve0 * denominator1 / denominator0 + reserve1);
        for (uint i = 0; i < precision; i++) {
            uint256 amount1 = queryBatchSwap(
                vault,
                IVault.SwapKind.GIVEN_IN,
                token0,
                token1,
                poolId0,
                amount1InToken0,
                address(this),
                address(this)
            );
            amount1InToken0 = (amount0Total * reserve1) / (reserve0 * amount1 / amount1InToken0 + reserve1);
        }
    }

    /**
     * Get amount of lp tokens where amount0Total is total getting amount nominated in token0
     *
     * precision: 0 - no correction, 1 - one correction (recommended value), 2 or more - several corrections
     */
    function getAmountLpTokens(
        IVault vault,
        IERC20 token0,
        IERC20 token1,
        bytes32 poolId0,
        uint256 amount0Total,
        uint256 totalLpBalance,
        uint256 reserve0,
        uint256 reserve1,
        uint256 denominator0,
        uint256 denominator1,
        uint256 precision
    ) internal returns (uint256 lpBalance) {
        lpBalance = (totalLpBalance * amount0Total) / (reserve0 + reserve1 * denominator0 / denominator1);
        for (uint i = 0; i < precision; i++) {
            uint256 amount1 = reserve1 * lpBalance / totalLpBalance;
            uint256 amount0 = queryBatchSwap(
                vault,
                IVault.SwapKind.GIVEN_IN,
                token1,
                token0,
                poolId0,
                amount1,
                address(this),
                address(this)
            );
            lpBalance = (totalLpBalance * amount0Total) / (reserve0 + reserve1 * amount0 / amount1);
        }
    }
