pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../connectors/uniswap/v2/interfaces/IUniswapV2Router02.sol";

abstract contract SpookySwapExchange {

    IUniswapV2Router02 private uniswapRouter;

    function setUniswapRouter(address _uniswapRouter) internal {
        uniswapRouter = IUniswapV2Router02(_uniswapRouter);
    }

    function swapTokenToUsdc(
        address swapToken,
        address usdcToken,
        uint256 swapTokenDenominator,
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (uint256) {

        uint256 amountOutMin = 0;
        IERC20(swapToken).approve(address(uniswapRouter), amount);

        address[] memory path = new address[](2);
        path[0] = swapToken;
        path[1] = usdcToken;

        return uniswapRouter.swapExactTokensForTokens(amount, amountOutMin, path, recipient, block.timestamp + 600)[1];
    }

    function getAmountsOut(
        address input,
        address output,
        uint256 amountInput
    ) internal view returns (uint256) {

        address[] memory path = new address[](2);
        path[0] = input;
        path[1] = output;

        uint[] memory amountsOut = uniswapRouter.getAmountsOut(amountInput, path);

        return amountsOut[1];
    }


    uint256[49] private __gap;
}
","keccak256":"0x3b311f879af3c40635c28853d5291d6419efc0d16d16b99513c6329b48e42488","license":"MIT"}},"version":1}
