    //     borrow1 = (totalBorrowUsd1 * HF + collateral1 * LT - totalCollateralUsd1 * LT) / (HF + LT * reserve0 / reserve1InToken0);
    // }

    // function getLpTokensForWithdraw(
    //     uint256 totalLpBalance,
    //     uint256 borrow1,
    //     uint256 reserve0,
    //     uint256 reserve1,
    //     uint256 token0Denominator,
    //     uint256 token1Denominator,
